import sys, os, pandas as pd
sys.path.append(os.path.abspath("./AI/combine"))
import modularization_ver2 as mo
txt = mo.extract_text_from_pdf('./Data_Analysis/Contract/24년 개정 직매입 표준거래계약서(면세점)-시계, 모두정상.pdf')
articles = mo.contract_to_articles_ver2(txt)
result=[]
for i,(article, content) in enumerate(articles.items()):
    print(article,summary_data[i][:3])
    result.append([content, summary_data[i].replace('“','').replace('”','').replace("'",'').replace('"','').replace('‘','').replace('’','')])

df = pd.DataFrame(result, columns=['input', 'summary'])
df.to_csv('./Data_Analysis/Data_ver2/summary_data/시계_summary_data.csv', index=False, encoding='utf-8-sig')